#include <stdio.h>
#define WIDTH (12)
#define HEIGHT (12)
#define BUFSIZE (16)

int solve(int map[HEIGHT][WIDTH]) {
	int x, y, n = 0;
	char set[HEIGHT * WIDTH] = {0};

	for (y = 0; y < HEIGHT; y++) {
		for (x = 0; x < WIDTH; x++) {
			if (!map[y][x]) {
				continue;
			}
			if (y && map[y-1][x]) {
				map[y][x] = map[y-1][x];
			}
			else if (x && map[y][x-1]) {
				map[y][x] = map[y][x-1];
			}
			else {
				n += 1;
				set[n] = 1;
				map[y][x] = n;
			}
		}
		for (x = 10; x >= 0; x--) {
			if (map[y][x] && map[y][x+1] && map[y][x] != map[y][x+1]) {
				set[map[y][x]] = 0;
				map[y][x] = map[y][x+1];
			}
		}
	}
	for (x = y = 0; y < HEIGHT * WIDTH; y++) {
		x += set[y];
	}
	return x;
}

int main(void) {
	int map[HEIGHT][WIDTH], x, y;
	char s[BUFSIZE];

	while (1) {
		for (y = 0; y < HEIGHT; y++) {
			fgets(s, BUFSIZE, stdin);
			for (x = 0; x < WIDTH; x++) {
				map[y][x] = s[x] - '0';
			}
		}
		printf("%d\n", solve(map));
		if (fgets(s, BUFSIZE, stdin) == NULL) {
			break;
		}
	}
	return 0;
}

