#define SIZE 12
#define Q_MAX 12*12

/* Queue */

int Q[Q_MAX], sp, rp;

void Qinit(){ sp=0, rp=0; }
void Qstore(int N, int M){ Q[sp++%Q_MAX]=N; Q[sp++%Q_MAX]=M; }
int Qretrive(){ return Q[rp++%Q_MAX]; }
int Qempty(){ return sp==rp; }

/* main */

int input[SIZE][SIZE], islands=0;

void analyzeIsland(int x, int y){	/* analyze an island */
	islands++, Qinit();

	input[x][y]=0; Qstore(x,y);
	while(!Qempty()){
		x=Qretrive(); y=Qretrive();
		if(x+1<SIZE && input[x+1][y]==1) input[x+1][y]=0, Qstore(x+1,y);
		if(y+1<SIZE && input[x][y+1]==1) input[x][y+1]=0, Qstore(x,y+1);
		if(x>0 && input[x-1][y]==1) input[x-1][y]=0, Qstore(x-1,y);
		if(y>0 && input[x][y-1]==1) input[x][y-1]=0, Qstore(x,y-1);
	}
}

