void b_island(int x, int y, char island[M_TATE][M_YOKO])
{
    if (island[y][x] != '1'){
        return;
    }
    island[y][x] = '0';
    if (x > 0){
        b_island(x - 1, y, island);
    }
    if (y > 0){
        b_island(x, y - 1, island);
    }
    if (x < 11){
        b_island(x + 1, y, island);
    }
    if (y < 11){
        b_island(x, y + 1, island);
    }
}

