#define N 12
char b[N+1][128];
int dx[]={ 1, -1, 0, 0 };
int dy[]={ 0, 0, -1, 1 };
void dfs(int y, int x)
{
    int r, c, k;
    b[y][x]='0';
    for (k=0; k<4; ++k) {
        r=y+dy[k];
        c=x+dx[k];
        if (r<0 || r>=N || c<0 || c>=N || b[r][c]=='0') continue;
        dfs(r, c);
    }
}
int main(void)
{
    while (1) {
        int i, j, res=0;
        for (i=0; i<N; ++i) if (!fgets(b[i], 128, stdin)) return 0;

        for (i=0; i<N; ++i) for (j=0; j<N; ++j)
            if (b[i][j]=='1') { res++; dfs(i, j); }
        printf("%d\n", res);

        if (!fgets(b[N], 128, stdin)) break;
    }
    return 0;
}

