char island[13][13];

void dfs(int i, int j){
	int di, dj;

	island[i][j] = '0';

	for (di = -1; di <= 1; di += 2){
		if (0 <= i + di && i + di < 12 && island[i + di][j] == '1'){
			dfs(i + di, j);
		}
	}
	for (dj = -1; dj <= 1; dj += 2){
		if (0 <= j + dj && j + dj < 12 && island[i][j + dj] == '1'){
			dfs(i, j + dj);
		}
	}
}

