			for(x=0;x<12;x++) {
				if(island[y][x]=='1') {
					count++;
					breakisland(x,y,island);
				}
			}
		}
		printf("%d\n",count);
	}
	return 0;
}

void breakisland(int x,int y,char island[12][13]) {
	if(island[y][x]!='1')return;
	island[y][x]='0';
	if(x>0)breakisland(x-1,y,island);
	if(y>0)breakisland(x,y-1,island);
	if(x<11)breakisland(x+1,y,island);
	if(y<11)breakisland(x,y+1,island);
}

#include <stdio.h>

char map[13][13];

void dfs(int x, int y)
{

	map[x][y] = '0';

	if (0 <= x + 1 && x + 1 < 12 && map[x + 1][y] == '1'){
		dfs(x + 1, y);
	}
	if (0 <= x - 1 && x - 1 < 12 && map[x - 1][y] == '1'){
		dfs(x - 1, y);
	}
	if (0 <= y + 1 && y + 1 < 12 && map[x][y + 1] == '1'){
		dfs(x, y + 1);
	}
	if (0 <= y - 1 && y - 1 < 12 && map[x][y - 1] == '1'){
		dfs(x, y - 1);
	}

	return;
}


int main(void)
{

	int w, l, island;
	char c;


	while (1){

		island = 0;
		for (w = 0; w < 12; w++){
			if (scanf("%s", map[w]) == EOF){
				return (0);
			}
		}


		for (w = 0; w < 12; w++){
			for (l = 0; l < 12; l++){

				if (map[w][l] == '1'){
					dfs(w, l);
					island++;
				}
			}
		}
		printf("%d\n", island);

	}

}

