import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayDeque;
import java.util.Queue;

/**
 * The Number of Island
 */
public class Main {

	final static int LAND = 1;
	final static int VISITED = -1;
	static Queue<Integer> xs, ys;
	static int count;

	public static void main(String[] args) throws IOException {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String line = "";

		while (true) {

			// load map
			int[][] map = new int[14][14];
			xs = new ArrayDeque<Integer>();
			ys = new ArrayDeque<Integer>();

			for (int i = 1; i < 13; i++) {
				line = br.readLine();
				for (int j = 1; j < 13; j++) {
					map[i][j] = line.charAt(j - 1) - '0';
				}
			}

			// solve
			count = 0;
			for (int i = 1; i < 13; i++) {
				for (int j = 1; j < 13; j++) {
					if (map[i][j] == LAND) {
						xs.add(i);
						ys.add(j);
						while (!xs.isEmpty() && !ys.isEmpty()) {
							visit(map);
						}
						count++;
					}
				}
			}
			// output
			System.out.println(count);

			// next map
			line = br.readLine();
			if (line == null) {
				break;
			} else {
				continue;
			}
		}
	}

	public static void visit(int[][] map) {

		final int[] V = { 1, -1, 0, 0 };
		final int[] H = { 0, 0, 1, -1 };

		int x, y;
		x = xs.remove();
		y = ys.remove();

		if (map[x][y] == LAND) {
			map[x][y] = VISITED;

			// next choices
			for (int i = 0; i < 4; i++) {
				int xv, yh;
				xv = x + V[i];
				yh = y + H[i];
				if (map[xv][yh] == LAND) {
					xs.add(xv);
					ys.add(yh);
				}
			}
		}
	}
}

import java.util.*;
import java.util.concurrent.*;

public class Main {
    private static Scanner sc = new Scanner(System.in);
    private static boolean[][] ary;
    private static boolean[][] b;
    public static void main(String[] args) throws Exception {
        while (sc.hasNext()) {
            ary = new boolean[12][12];
            b = new boolean[12][12];
            solve();
        }
    }

    private static void solve() {
        for (int i = 0;i < 12;i++) {
            String s = sc.next();
            for (int j = 0;j < 12;j++) {
                if (s.charAt(j)=='1') {
                    ary[i][j] = true;
                }
            }
        }

        int ans = 0;
        for (int i = 0;i < 12;i++) {
            for (int j = 0;j < 12;j++) {
                if (ary[i][j]&&!b[i][j]) {
                    move(i,j);
                    ans++;
                }
            }
        }

        System.out.println(ans);
    }

    private static void move(int x, int y) {
        int[] dx = {0, 1, 0, -1};
        int[] dy = {1, 0, -1, 0};

        b[x][y] = true;

        for (int i = 0;i < 4;i++) {
            int nx = x + dx[i];
            int ny = y + dy[i];
            if (range(nx,ny)) {
                if (ary[nx][ny]&&!b[nx][ny]) {
                    move(nx, ny);
                }
            }
        }
    }

    private static boolean range(int x, int y) {
        return 0<=x&&x<12&&0<=y&&y<12;
    }
}

