import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.io.PrintStream;

class Main {

    public static void main(String[] args) throws IOException {
        doit(args, System.in, System.out);
    }

    static void doit(String[] args, InputStream in, PrintStream out)
            throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(in));
        while (true) {
            int[][] map = new int[14][14];
            for (int y = 1; y <= 12; y++) {
                String line = reader.readLine();
                for (int x = 1; x <= 12; x++) {
                    map[y][x] = line.charAt(x - 1) - '0';
                }
            }
            int result = 0;
            for (int y = 1; y <= 12; y++) {
                for (int x = 1; x <= 12; x++) {
                    if (map[y][x] == 0) {
                        continue;
                    }
                    erase(map, x, y);
                    result++;
                }
            }
            out.println(result);
            if (reader.readLine() == null) {
                break;
            }
        }
    }

    private static void erase(int[][] map, int x, int y) {
        if (map[y][x] == 0) {
            return;
        }
        map[y][x] = 0;
        erase(map, x + 1, y);
        erase(map, x - 1, y);
        erase(map, x, y + 1);
        erase(map, x, y - 1);
    }
}

import java.util.Scanner;



public class Main {
	final int N = 12;
	final int[][] ofs = {
			{1, 0}, {0,-1}, {-1, 0}, {0, 1}
	};
	void del(boolean[][] map, int y, int x){
		map[y][x] = false;
		for(int i=0; i<4; ++i){
			int nx = x + ofs[i][0];
			int ny = y + ofs[i][1];
			if(0<=ny && ny<N && 0<=nx && nx<N){
				if(map[ny][nx]){
					del(map, ny, nx);
				}
			}
		}
	}

	int solve(boolean map[][]){
		int c = 0;

		for(int y=0; y<N; ++y){
			for(int x=0; x<N; ++x){
				if(map[y][x]){
					c++;
					del(map, y, x);
				}
			}
		}

		return c;
	}

	void io(){
		Scanner sc = new Scanner(System.in);
		while(sc.hasNext()){
			char[][] str = new char[N][N];
			for(int i=0; i<N; ++i){
				str[i] = sc.nextLine().toCharArray();
			}

			boolean[][] map = new boolean[N][N];
			for(int y=0; y<N; ++y){
				for(int x=0; x<N; ++x){
					map[y][x] = str[y][x]=='1';
				}
			}

			System.out.println(solve(map));
			if(sc.hasNext()){ sc.nextLine(); }//から読み
		}
	}

	public static void main(String[] args) {
		new Main().io();
	}
}

