import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.LinkedList;

/**
 * The Number of Island
 */
public class Main {

	final static int SEA = 0;
	final static int LAND = 1;
	final static int VISITED = -1;

	final static int[] V = { 1, -1, 0, 0 };
	final static int[] H = { 0, 0, 1, -1 };

	static LinkedList<Integer> xs, ys;
	static int count;

	public static void main(String[] args) throws IOException {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String line = "";

		int[][] map = new int[12][12];

		while (true) {
			// load map
			xs = new LinkedList<Integer>();
			ys = new LinkedList<Integer>();
			for (int i = 0; i < 12; i++) {
				line = br.readLine();
				for (int j = 0; j < 12; j++) {
					map[i][j] = line.charAt(j) - '0';
				}
			}

			// solve
			count = 0;
			for (int i = 0; i < 12; i++) {
				for (int j = 0; j < 12; j++) {
					if (map[i][j] == LAND) {
						xs.add(i);
						ys.add(j);
						while (!xs.isEmpty() && !ys.isEmpty()) {
							visit(map);
						}
						count++;
					}
				}
			}

			// output.
			System.out.println(count);

			// next
			line = br.readLine();
			if (line == null) {
				break;
			} else {
				continue;
			}
		}
		//
	}

	public static void visit(int[][] map) {

		int x, y;
		x = xs.remove();
		y = ys.remove();

		map[x][y] = VISITED;

		// next choices
		for (int i = 0; i < 4; i++) {
			int xv, yh;
			xv = x + V[i];
			yh = y + H[i];
			if (xv >= 0 && xv < 12 && yh >= 0 && yh < 12
					&& map[xv][yh] != VISITED && map[xv][yh] != SEA) {
				xs.add(xv);
				ys.add(yh);
			}
		}
	}
}

import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String args[]) {
        Scanner sc = new Scanner(System.in);
        while (sc.hasNext()) {
            char[][] island = new char[12][12];
            int[][] map = new int[12][12];
            for (int i = 0; i < 12; i++) {
                Arrays.fill(map[i], 999);
            }
            for (int i = 0; i < 12; i++) {
                island[i] = sc.next().toCharArray();
            }
            int count = 0;
            for (int i = 0; i < 12; i++) {
                for (int j = 0; j < 12; j++) {
                    if (island[i][j] != '0') {
                        if (i == 0 && j == 0) {
                            map[i][j] = count++;
                        } else {
                            if (i - 1 >= 0 && island[i][j] == island[i - 1][j]) {
                                if (map[i][j] >= 0) {
                                    map[i][j] = Math.min(map[i][j], map[i - 1][j]);
                                }
                            } else if (j - 1 >= 0 && island[i][j] == island[i][j - 1]) {
                                map[i][j] = Math.min(map[i][j], map[i][j - 1]);
                            } else if (j + 1 < 12 && island[i][j] == island[i][j + 1]) {
                                for (int k = j; k < 11; k++) {
                                    if (i - 1 >= 0 && island[i][k] == island[i][k + 1] && island[i][k + 1] == '1' && island[i - 1][k + 1] == '1') {
                                        map[i][j] = map[i - 1][k + 1];
                                        count--;
                                        break;
                                    }
                                }
                                map[i][j] = count++;
                            } else {
                                map[i][j] = count++;
                            }
                        }
                    }
                }
            }
            System.out.println(count);
        }
    }
}

import java.io.IOException;
import java.io.BufferedReader;
import java.io.InputStreamReader;

class Main {
    static int[][] map = new int[12][12];

    static void removeIslands(int i, int j){
        if(i < 0 || i > 11 || j < 0 || j > 11) return;
        if(map[i][j] == 1){
            map[i][j] = 0;
            removeIslands(i-1,j);
            removeIslands(i+1,j);
            removeIslands(i,j+1);
            removeIslands(i,j-1);
        }else{
            return;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String line;

        while ((line = br.readLine()) != null) {
            // initialize map
            for (int i = 0; i < 12; i++) {
                char[] c = line.toCharArray();
                for (int j = 0; j < 12; j++) {
                    if (c[j] == '1') {
                        map[i][j] = 1;
                    } else {
                        map[i][j] = 0;
                    }
                }
                line = br.readLine();
            }

            int answer = 0;
            // counting islands
            for (int i = 0; i < 12; i++) {
                for (int j = 0; j < 12; j++) {
                    if (map[i][j] == 1) {
                        answer++;
                        removeIslands(i, j);
                    }
                }
            }

            System.out.println(answer);

        }
    }
}

