		public Island(int[][] map) {
			this.map = map;
		}

		private void label(int row, int column) {
			map[row][column] = num;
			for(int i=0; i<4; i++) {
				if(i==0) {
					if(map[row][column-1] == 1) label(row, column-1);
				} else if(i == 1) {
					if(map[row-1][column] == 1) label(row-1, column);
				} else if(i == 2) {
					if(map[row][column+1] == 1) label(row, column+1);
				} else if(i == 3) {
					if(map[row+1][column] == 1) label(row+1, column);
				}
			}
		}

		public int count() {
			for(int i=0; i<12; i++) {
				for(int j=0; j<12; j++) {
					if(map[i][j] == 1) {
						num++;
						label(i, j);
					}
				}
			}
			return num-2;
		}

	}


