	public static int[][] checkIsland(int[][] area,int i,int j) {
		area[i][j] = 0;

		if(j < 11) {
			if(area[i][j + 1] == 1) {
				area = checkIsland(area, i,j + 1);
			}
		}
		if(i < 11) {
			if(area[i + 1][j] == 1) {
				area = checkIsland(area,i + 1, j);
			}
		}
		if(j > 0) {
			if(area[i][j - 1] == 1) {
				area = checkIsland(area, i,j - 1);
			}
		}
		if(i > 0) {
			if(area[i - 1][j] == 1) {
				area = checkIsland( area, i - 1, j);
			}
		}

		return area;

	}
}

import java.io.BufferedReader;
