class IslandMap{
	static int[][] sima;
	int tate;
	int yoko;


	public IslandMap(int i, int j, int[][] sima2) {
		this.tate = i;
		this.yoko = j;
		IslandMap.sima = sima2;
	}


	public static void check(int tate , int yoko, int[][] sima){

		sima[tate][yoko] = 0;

		if(tate+1 < 12){
			if(sima[tate+1][yoko] == 1){
				check(tate+1 , yoko , sima);
			}
		}
		if(yoko+1 < 12){
			if(sima[tate][yoko+1] == 1){
				check(tate , yoko+1 , sima);
			}
		}
		if(tate-1 > -1){
			if(sima[tate-1][yoko] == 1){
				check(tate-1 , yoko , sima);
			}
		}
		if(yoko-1 > -1){
			if(sima[tate][yoko-1] == 1){
				check(tate , yoko-1 , sima);
			}
		}
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayDeque;
import java.util.Queue;

/**
 * The Number of Island
 */
public class Main {

    final static int LAND = 1;
    final static int VISITED = -1;
    static Queue<Integer> xs, ys;
    static int count;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String line = "";

        while (true) {

            // load map
            int[][] map = new int[14][14];
            xs = new ArrayDeque<Integer>();
            ys = new ArrayDeque<Integer>();

            for (int i = 1; i < 13; i++) {
                line = br.readLine();
                for (int j = 1; j < 13; j++) {
                    map[i][j] = line.charAt(j - 1) - '0';
                }
            }

            // solve
            count = 0;
            for (int i = 1; i < 13; i++) {
                for (int j = 1; j < 13; j++) {
                    if (map[i][j] == LAND) {
                        xs.add(i);
                        ys.add(j);
                        while (!xs.isEmpty() && !ys.isEmpty()) {
                            visit(map);
                        }
                        count++;
                    }
                }
            }
            // output
            System.out.println(count);

            // next map
            line = br.readLine();
            if (line == null) {
                break;
            } else {
                continue;
            }
        }
    }

    public static void visit(int[][] map) {

        final int[] V = { 1, -1, 0, 0 };
        final int[] H = { 0, 0, 1, -1 };

        int x, y;
        x = xs.remove();
        y = ys.remove();

        if (map[x][y] == LAND) {
            map[x][y] = VISITED;

            // next choices
            for (int i = 0; i < 4; i++) {
                int xv, yh;
                xv = x + V[i];
                yh = y + H[i];
                if (map[xv][yh] == LAND) {
                    xs.add(xv);
                    ys.add(yh);
                }
            }
        }
    }
}

import java.util.Scanner;
public class Main {
	boolean[][] board;
	int[] fdx = new int[]{1, -1, 0, 0};
	int[] fdy = new int[]{0, 0, -1, 1};

	void dfs(int x, int y) {
		if(x < 0 || y < 0 || x >= 12 || y >= 12) return;
		if(!board[x][y]) return;
		if(board[x][y]) board[x][y] = false;
		for(int r = 0; r < fdx.length; r++) {
			dfs(x + fdx[r], y + fdy[r]);
		}
	}

	void doIt() {
		Scanner stdIn = new Scanner(System.in);
		board = new boolean[12][12];
		for(int r = 0; r < 12; r++) {
			String input = stdIn.next();
			for(int c = 0; c < 12; c++) {
				if(input.charAt(c) == '1') board[r][c] = true;
			}
		}
		int count = 0;
		while(true) {
			boolean flag = false;
			for(int r = 0; r < 12; r++) {
				for(int c = 0; c < 12; c++) {
					if(board[r][c]) {
						flag = true;
						dfs(r, c);
						count++;
						break;
					}
					if(flag) break;
				}
			}
			if(!flag) break; //見つからなかったら既に探索が完了している。
		}
		System.out.println(count);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		new Main().doIt();
	}
}

