		Scanner sc = new Scanner(System.in);
		for(int i=0; i<20; i++){
			board = new boolean[12][12];
			for(int j=0; j<12; j++){
				String s = sc.next();
				for(int k=0; k<12; k++){
					if(s.charAt(k)=='1')board[j][k]=false;
					else board[j][k]=true;
				}
			}
			int count=0;
			for(int j=0; j<12; j++){
				for(int k=0; k<12; k++){
					if(!board[j][k]){
						count++;
						dfs(k,j);
					}
				}
			}
			System.out.println(count);
		}
	}

	static void dfs(int x, int y){
		if(board[y][x])return;
