			board[y][x]=true;
			for(int i=0; i<4; i++){
				int nextx = x+xx[i];
				int nexty = y+yy[i];
				if(nextx>=0 && nextx<12 && nexty>=0 && nexty<12){
					dfs(nextx,nexty);
				}
			}
		}
	}
