	    }
	}catch (Exception e){
	    System.out.println("Error");
	}
    }

    private static void solve(int[][] map, int j, int i){
	if(map[j][i]==0)
	    return;
	map[j][i] = 0;
	solve(map, j, i+1);
	solve(map, j, i-1);
	solve(map, j+1, i);
	solve(map, j-1, i);
    }
}

