import java.io.PrintWriter;
import java.util.Scanner;
public class Main {
	static boolean[][] judge;
	static int[] fieldX = {-1,0,1,0}, 
			fieldY = {0,-1,0,1};
	static void doing(int x, int y) {
		judge[y][x] = false;
		for (int i = 0; i < 4; i++) {
			if (judge[y+fieldY[i]][x+fieldX[i]]) doing(x + fieldX[i], y + fieldY[i]);
		}
	}

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		PrintWriter out = new PrintWriter(System.out);
		while (sc.hasNext()) {
			judge = new boolean[20][20];
			for (int i = 1; i <= 12; i++){
				String temp = sc.next();
				for (int x = 1; x <= 12; x++) {
					judge[i][x] = temp.charAt(x - 1) == '1';
				}
			}
			int cnt = 0;
			for (int i = 1; i <= 12; i++) {
				for (int x = 1; x <= 12; x++) {

					if (judge[i][x]){ cnt++; doing(x, i);
					}

				}
			}
			out.println(cnt);
		}
		out.flush();
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayDeque;
import java.util.Queue;

/**
 * The Number of Island
 */
public class Main {

	final static int LAND = 1;
	final static int VISITED = -1;

	final static int[] V = { 1, -1, 0, 0 };
	final static int[] H = { 0, 0, 1, -1 };

	static Queue<Integer> xs, ys;
	static int count;

	public static void main(String[] args) throws IOException {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String line = "";

		StringBuilder sb = new StringBuilder();
		while (true) {

			// load map
			int[][] map = new int[14][14];
			xs = new ArrayDeque<Integer>();
			ys = new ArrayDeque<Integer>();

			for (int i = 1; i < 13; i++) {
				line = br.readLine();
				for (int j = 1; j < 13; j++) {
					map[i][j] = line.charAt(j - 1) - '0';
				}
			}

			// solve
			count = 0;
			for (int i = 1; i < 13; i++) {
				for (int j = 1; j < 13; j++) {
					if (map[i][j] == LAND) {
						xs.add(i);
						ys.add(j);
						while (!xs.isEmpty() && !ys.isEmpty()) {
							visit(map);
						}
						count++;
					}
				}
			}
			sb.append(count + "\n");

			// next map
			line = br.readLine();
			if (line == null) {
				break;
			} else {
				continue;
			}
		}
		// output
		System.out.print(sb.toString());
	}

	public static void visit(int[][] map) {

		int x, y;
		x = xs.remove();
		y = ys.remove();

		if (map[x][y] == LAND) {
			map[x][y] = VISITED;

			// next choices
			for (int i = 0; i < 4; i++) {
				int xv, yh;
				xv = x + V[i];
				yh = y + H[i];
				if (map[xv][yh] == LAND) {
					xs.add(xv);
					ys.add(yh);
				}
			}
		}
	}
}

