class Human {

	int x;
	int y;//humanの現在の座標
	Human(){
		x= 0;
		y= 0;
	}
	boolean[][] map = new boolean[12][12]; //地図


	public void Advent(int x, int y){


		map[x][y]=false;//行ったことのある場所は海に

		//再帰処理で陸続きの場所をすべて沈める
		if(x<11){
			if(map[x+1][y]){
				Advent(x+1,y);
			}
		}
		if(x>0){
			if(map[x-1][y]){
				Advent(x-1,y);
			}
		}
		if(y<11){
			if(map[x][y+1]){
				Advent(x,y+1);
			}
		}
		if(y>0){
			if(map[x][y-1]){
				Advent(x,y-1);
			}
		}

	}



}

