            int i2 = i + move[k][0];
            int j2 = j + move[k][1];
            if (0 <= i2 && i2 < 12 && 0 <= j2 && j2 < 12 && map[i2][j2])
                dfs(i2, j2);
        }
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        while (sc.hasNext()) {
            map = new boolean[12][12];
