 class Main{
             static int[][] map=new int[12][12];
             static boolean[][] counted=new boolean[12][12];
     public static void main(String args[]){
         BufferedReader br = new BufferedReader(new InputStreamReader(System.in));

         try{
             String line;
             while((line=br.readLine())!= null){if(line.isEmpty())continue;
                for(int i=0;i<12;i++)map[0][i]=Integer.parseInt(line.charAt(i)+"");
                for(int i=1;i<=11;i++){
                    line=br.readLine();
                    for(int j=0;j<12;j++)map[i][j]=Integer.parseInt(line.charAt(i)+"");
                }for(int i=0;i<12;i++)for(int j=0;j<12;j++)counted[i][j]=false;

                int islanC=0;
                for(int i=0;i<12;i++){
                    for(int j=0;j<12;j++){
                        if(map[i][j]!=0&&!counted[i][j]){islanC++;countIsl(i,j);}
                    }
                }
                 System.out.println(islanC);


             }

         }catch(Exception e){}         
     }
     static void countIsl(int y,int x){
         if(counted[y][x])return; else{counted[y][x]=true;}
         if(y!=0)if(map[y-1][x]!=0)countIsl(y-1,x);
         if(y!=11)if(map[y+1][x]!=0)countIsl(y+1,x);
         if(x!=11)if(map[y][x+1]!=0)countIsl(y,x+1);
         if(x!=0)if(map[y][x-1]!=0)countIsl(y,x-1);
     }


 }

import java.util.*;
public class Main{
    public static int M=12;
      public static int N=12;
    public static void main(String[] a)throws java.io.IOException{

    Scanner scan=new Scanner(System.in);
    while(scan.hasNext()){
                    int ans =0;
                    int [][] field =new int[M][N];
                    for(int i=0;i<M;i++){
                     String line = scan.nextLine();
                        for(int j=0;j<N ;j++){
                            field[i][j] = line.charAt(j);

                        }
                    }


                    for(int i=0;i<M;i++){
                        for(int j=0;j<N;j++){
                            if(field[i][j] != '0'){
                                ++ans;
                                dfs(i,j,field);

                            }
                        }
                    }
                    System.out.println(ans);
                    scan.nextLine();

            }
    }


          public static void dfs(int y,int x,int [][] field){
              int crop = field[y][x];
              field[y][x] = '0';
              int [] dx = {1,0,-1,0};
              int [] dy ={0,1,0,-1};
              for(int i=0;i<4;i++){
                  int nx =x+dx[i];
                  int ny =y+dy[i];
                  if(0<=nx && nx< N &&0<=ny && ny <M  &&( field[ny][nx]==crop))
                      dfs(ny,nx,field);
              }
              return;
          }
}

