	static int num,STATE[][];
	public static void main(String[]args){
		sc=new Scanner(System.in);
		ISCHK=new boolean[14][14];
		STATE=new int[14][14];
		String tmpstr=new String("");
		while(sc.hasNext()){
			num=0;
			for(int i=0;i<14;i++){
				if(i>0 && i<13){tmpstr=sc.next();}
				for(int j=0;j<14;j++){
					if(i>0 && i<13){
						if(j==0||j==13){
							STATE[i][j]=0;
							ISCHK[i][j]=true;
						}else if(j<13){
							ISCHK[i][j+1]=false;
							STATE[i][j]=Integer.parseInt(tmpstr.substring(j-1,j));
						}
					}else{
							STATE[i][j]=0;
							ISCHK[i][j]=true;
					}
				}
			}
			for(int i=1;i<13;i++){
				for(int j=1;j<13;j++){
					if(ISCHK[i][j]==false){
						if(STATE[i][j]==0){
							ISCHK[i][j]=true;
						}else{
							ISCHK[i][j]=true;
							num++;
							srch(i,j);
						}
					}
				}
			}
			System.out.println(num);
		}
	}
	static void srch(int nowX,int nowY){
		if(ISCHK[nowX-1][nowY]==false && STATE[nowX-1][nowY]==1){
			ISCHK[nowX-1][nowY]=true;
			srch(nowX-1,nowY);
		}
		if(ISCHK[nowX][nowY-1]==false && STATE[nowX][nowY-1]==1){
			ISCHK[nowX][nowY-1]=true;
			srch(nowX,nowY-1);
		}
		if(ISCHK[nowX+1][nowY]==false && STATE[nowX+1][nowY]==1){
			ISCHK[nowX+1][nowY]=true;
			srch(nowX+1,nowY);
		}
		if(ISCHK[nowX][nowY+1]==false && STATE[nowX][nowY+1]==1){
			ISCHK[nowX][nowY+1]=true;
			srch(nowX,nowY+1);
		}
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayDeque;
import java.util.Deque;
import java.util.Scanner;

public class Main {

	public static void main(String[] args) throws NumberFormatException,
			IOException {
		BufferedReader reader = new BufferedReader(new InputStreamReader(
				System.in));
		StringBuilder builder = new StringBuilder();
		Scanner scanner = new Scanner(System.in);
		int n = 12;
		int l = 12;
		while (scanner.hasNext()) {
			boolean[][] map = new boolean[l][n];
			for (int i = 0; i < l; i++) {
				String strs = scanner.nextLine();
				for (int j = 0; j < n; j++) {
					if (strs.charAt(j) == '1') {
						map[i][j] = true;
					}
				}
			}
			int count = 0;
			for (int i = 0; i < l; i++) {
				for (int j = 0; j < n; j++) {
					if (map[i][j]) {
						count++;
						dfs(i, j, map, n, l);
					}
				}
			}
			scanner.nextLine();
			System.out.println(count);
		}
	}

	private static void dfs(int y, int x, boolean[][] map, int n, int l) {
		int[] dx = { -1, 0, 0, 1 };
		int[] dy = { 0, 1, -1, 0 };
		Deque<int[]> deque = new ArrayDeque<int[]>();
		deque.push(new int[] { y, x });

		while (!deque.isEmpty()) {
			int[] a = deque.pop();
			int ny = a[0];
			int nx = a[1];
			map[ny][nx] = false;
			for (int i = 0; i < 4; i++) {
				int py = ny + dy[i];
				int px = nx + dx[i];
				if (0 <= px && px < n && 0 <= py && py < l && map[py][px]) {
					deque.push(new int[] { py, px });
				}
			}
		}
	}
}

