import java.io.*;
import java.util.*;

class Main{
    public static void main(String[] args){
	BufferedReader sc=new BufferedReader(new InputStreamReader(System.in));
	try{
	    String st;
	    while((st=sc.readLine())!=null){
		int[][] map = new int[14][14];
		int count = 0;
		for(int i=1; i<=12; i++)
		    map[1][i] = st.charAt(i-1) - '0';
		for(int j=2; j<=12; j++){
		    st = sc.readLine();
		    for(int i=1; i<=12; i++)
			map[j][i] = st.charAt(i-1) - '0';
		}
		for(int j=1; j<=12; j++)
		    for(int i=1; i<=12; i++)
			if(map[j][i]==1){
			    solve(map, j, i);
			    count++;
			}
		System.out.println(count);
		st = sc.readLine();
		if(st == null)
		    break;
	    }
	}catch (Exception e){
	    System.out.println("Error");
	}
    }

    private static void solve(int[][] map, int j, int i){
	if(map[j][i]==0)
	    return;
	map[j][i] = 0;
	solve(map, j, i+1);
	solve(map, j, i-1);
	solve(map, j+1, i);
	solve(map, j-1, i);
    }
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {
public static void main(String[] args) throws IOException {
  BufferedReader br = new BufferedReader(
	  new InputStreamReader(System.in));
  boolean hasNext = true;
  while (hasNext) {
	int[][] before = new int[12][];
	int[][] line = new int[12][];
	int count = 0, num = 0;
	int[][] old = null;
	char[] read = new char[12];
	for (int col = 0; col < 12; col++) {
	  old = before;
	  before = line;
	  line = old;
	  br.read(read);
	  for (int row = 0; row < 12; row++) {
		if (read[row] == '1') {
		  boolean upperHas = before[row] != null;
		  boolean leftHas = row != 0 && line[row - 1] != null;
		  if (!(upperHas || leftHas)) {
			line[row] = new int[] { num++ };
			count++;
		  } else if (leftHas) {
			line[row] = line[row - 1];
			if (upperHas && line[row][0] != before[row][0]) {
			  line[row][0] = before[row][0];
			  count--;
			}
		  } else if (upperHas) {
			line[row] = before[row];
		  }
		}
		else {
		  line[row] = null;
		}
	  }
	  br.readLine();
	}
	System.out.println(count);
	hasNext = br.readLine() != null;
  }
}
}

