    static void dfs(int i, int j){
        if(0 <= i && i < 12 && 0 <= j && j < 12){
            if(m[i][j] == 1){
                m[i][j] = -1;
                dfs(i + 1, j);
                dfs(i - 1, j);
                dfs(i, j + 1);
                dfs(i, j - 1);
            }
        }
    }

