       Scanner sc = new Scanner(System.in);
       while(sc.hasNext()){
           for(int i = 0; i < 12; i++){
               String s = sc.next();
               for(int j = 0; j < 12; j++){
                   String c = s.substring(j, j + 1);
                   if(c.equals("1")) m[i][j] = 1;
                   else m[i][j] = 0;
               }
           }
           int cnt = 0;
           for(int i = 0; i < 12; i++){
               for(int j = 0; j < 12; j++){
                   if(m[i][j] == 1){
                       cnt++;
                       dfs(i, j);
                   }
               }
           }
           System.out.println(cnt);
       }
   }
