			for(int i = 0; i < 12; i++) {
				int count = 0;
				for( String s : scan.nextLine().split("",0)) {
					if(s.equals("1")) {
						map[i][count] = true;
					}
					else {
						map[i][count] = false;
					}
					count++;
				}
			}
			for(int i = 0; i < 12 ; i++) {
				for(int u = 0; u < 12; u++) {
					if(map[i][u] && !check[i][u]) {
						fun(i,u);
					}
				}
			}
		}
	}

