		// TODO Auto-generated method stub
		String	stdin  = null;
		String	prev   = "000000000000";
		int		island = 0;
		int		len    = prev.length();

		while ((stdin = parseStdin()) != null) {
			if (stdin.isEmpty()) {
				System.out.println(island);
				prev   = "000000000000";
				island = 0;
				continue;
			}

			if (stdin.contains("1")) {
				for (int i = 0; i < len; i++) {
					if (stdin.charAt(i) == '1') {
						boolean	isnew = true;

						for (int j = i; j < len; j++, i++) {
							if (stdin.charAt(j) != '1') {
								break;
							}
							isnew = (prev.charAt(j) == '1') ? false : isnew;
						}

						island = (isnew) ? island + 1: island;
					}
				}
			}

			prev = stdin;
		}
	}

	private static String parseStdin() {
		String	stdin = null;

		try {
			stdin = br.readLine();
		}
		catch (IOException e) {}

		return stdin;
	}
