      for(i = 1;i < 13;i++)for(j = 1;j < 13;j++)if(f[i][j] == '1'){
        count++;
        f[i][j] = '0';
        pushhw(nowh, noww, i, j);
        for(;top > 0;){
          h = poph(nowh); w = popw(noww);
          for(k = 0;k < 4;k++)if(f[h + di[k]][w + dj[k]] == '1'){
            f[h + di[k]][w + dj[k]] = '0';
            pushhw(nowh, noww, h + di[k], w + dj[k]);
          }
        }
      }

      out.println(count);
    }

    sc.close();
  }
