						}else if(island[i][j+1]==1) {//right
							island[i][j+1]=2;
							if(j==10) {
							}else if(island[i][j+2]==1) {//right_right
								island[i][j+2]=3;
							}
							if(i==11) {
							}else if(island[i+1][j+1]==1) {//right_under
								island[i+1][j+1]=3;
							}
						}
