public class Main {
	static int island[][] = new int[12][12];

	public static void main(String[] args) {
		// TODO 自動生成されたメソッド・スタブ
		Scanner scanner = new Scanner(System.in);
		boolean start = true;
		while (start) {
			// 島を配列islandで定義
			// 1か0を入力し島を形成
			for (int length = 0; length < 12; length++) {
				String line = scanner.nextLine();
				for (int side = 0; side < 12; side++) {
					char area = line.charAt(side);
					island[length][side] = Character.getNumericValue(area);
				}
			}

			// 島の数islandの宣言、初期化
			int islandcount = 0;
			// 島を見つける
			for (int length = 0; length < 12; length++) {
				for (int side = 0; side < 12; side++) {
					if (island[length][side] == 1) {
						islandcount++;
						islandnumber(length, side);
						island[length][side] = -1;
					}
				}
			}
			System.out.println(islandcount);
			if (scanner.hasNext()) {
				String space = scanner.nextLine();
			} else {
				start = false;
			}
		}
	}

	static void islandnumber(int a, int b) {
		island[a][b] = -5;
		if (a != island.length - 1 && island[a + 1][b] == 1) {
			islandnumber(a + 1, b);
		}
		if (a != 0 && island[a - 1][b] == 1) {
			islandnumber(a - 1, b);
		}
		if (b != island.length - 1 && island[a][b + 1] == 1) {
			islandnumber(a, b + 1);
		}
		if (b != 0 && island[a][b - 1] == 1) {
			islandnumber(a, b - 1);
		}
	}
}

import java.util.Scanner;
public class Main
{
	static int base[][]=new int[12][12];
	static int ans;
	public static void main(String arg[])
	{
		Scanner in=new Scanner(System.in);
		while(in.hasNext())
		{

			String str;
			for(int i=0;i<12;i++)
			{
				str=in.next();
				for(int j=0;j<12;j++)
				{
					if(str.charAt(j)=='0')
						base[i][j]=0;
					else if(str.charAt(j)=='1')
						base[i][j]=1;
				}
			}
			ans=0;
			for(int i=0;i<12;i++)
				for(int j=0;j<12;j++)
				{
					if(base[i][j]==1)
					{
						solve(i,j);
						ans++;
					}
				}
			System.out.println(ans);

		}
	}
	public static void solve(int m, int n)
	{
			if(base[m][n]==1)
			{
				base[m][n]=2;
				if(m-1>=0&&base[m-1][n]==1)
					solve(m-1,n);
				if(m+1<12&&base[m+1][n]==1)
					solve(m+1,n);
				if(n-1>=0&&base[m][n-1]==1)
					solve(m,n-1);
				if(n+1<12&&base[m][n+1]==1)
					solve(m,n+1);
			}
	}
}

