public class Pg
{
    public static void Main()
    {
        List<List<int>> map = new List<List<int>>();
        const int DATUM = 11;
        List<int> ans = new List<int>();
        for (string line; !string.IsNullOrEmpty(line = Console.ReadLine()); map.Clear())
        {
            map.Add(line.ToCharArray().Select(x => int.Parse(x.ToString())).ToList());
            for (int i = 0; i < DATUM; ++i)
            {
                map.Add(Console.ReadLine().ToCharArray().Select(x => int.Parse(x.ToString())).ToList());
            }
            var op = new Operate(map);
            ans.Add(op.GetNumsOfIslands());
        }
        Console.WriteLine(string.Join("\n", ans));
    }
}
class Operate
{
    List<List<int>> Map { get; set; }
    internal Operate(List<List<int>> map)
    {
        Map = map;
        Console.ReadLine();
    }
    internal int GetNumsOfIslands()
    {
        int cnt = 0;
        for (int i = 0; i < 12; i++)
        {
            for (int j = 0; j < 12; j++)
            {
                if (Map[i][j] == 1)
                {
                    IsSpreadMore(i, j);
                    ++cnt;
                }
            }
        }
        return cnt;
    }

    private void IsSpreadMore(int i, int j)
    {
        Map[i][j]--;
        if (j != 0 && Map[i][j - 1] == 1)
        {
            IsSpreadMore(i, j - 1);
        }
        if (j != 11 && Map[i][j + 1] == 1)
        {
            IsSpreadMore(i, j + 1);
        }
        if (i != 0 && Map[i - 1][j] == 1)
        {
            IsSpreadMore(i - 1, j);
        }
        if (i != 11 && Map[i + 1][j] == 1)
        {
            IsSpreadMore(i + 1, j);
        }
    }
}

