using System;
using System.Collections.Generic;

namespace AOJ.Volume0
{
    public class TheNumberOfIsland
    {
        private struct Point
        {
            public int X;
            public int Y;
        }

        private static int CountIsland(int[,] map)
        {
            int ret = 0;
            for (int i = 1; i <= 12; i++)
            {
                for (int j = 1; j <= 12; j++)
                {
                    if (map[i, j] == 1)
                    {
                        ret++;
                        map[i, j] = 0;

                        // つながっている島を探して消していく
                        var q = new Queue<Point>();
                        if (map[i + 1, j] == 1)
                        {
                            q.Enqueue(new Point { X = i + 1, Y = j });
                            map[i + 1, j] = 0;
                        }
                        if (map[i - 1, j] == 1)
                        {
                            q.Enqueue(new Point { X = i - 1, Y = j });
                            map[i - 1, j] = 0;
                        }
                        if (map[i, j + 1] == 1)
                        {
                            q.Enqueue(new Point { X = i, Y = j + 1 });
                            map[i, j + 1] = 0;
                        }
                        if (map[i, j - 1] == 1)
                        {
                            q.Enqueue(new Point { X = i, Y = j - 1 });
                            map[i, j - 1] = 0;
                        }
                        while (q.Count > 0)
                        {
                            var p = q.Dequeue();
                            // 上下左右の島を探す
                            if (map[p.X + 1, p.Y] == 1)
                            {
                                q.Enqueue(new Point { X = p.X + 1, Y = p.Y });
                                map[p.X + 1, p.Y] = 0;
                            }
                            if (map[p.X - 1, p.Y] == 1)
                            {
                                q.Enqueue(new Point { X = p.X - 1, Y = p.Y });
                                map[p.X - 1, p.Y] = 0;
                            }
                            if (map[p.X, p.Y + 1] == 1)
                            {
                                q.Enqueue(new Point { X = p.X, Y = p.Y + 1 });
                                map[p.X, p.Y + 1] = 0;
                            }
                            if (map[p.X, p.Y - 1] == 1)
                            {
                                q.Enqueue(new Point { X = p.X, Y = p.Y - 1 });
                                map[p.X, p.Y - 1] = 0;
                            }
                        }
                    }
                }
            }

            return ret;
        }

        public static void Main()
        {
            while (true)
            {
                string input = Console.ReadLine();
                if (string.IsNullOrEmpty(input)) { break; }

                // 地図を初期化
                var map = new int[14, 14];
                // 1行目の処理
                for (int i = 1; i <= 12; i++) { map[1, i] = int.Parse(input.Substring(i - 1, 1)); }
                // 2行目?12行目の読み込み
                for (int i = 2; i <= 12; i++)
                {
                    input = Console.ReadLine();
                    for (int j = 1; j <= 12; j++) { map[i, j] = int.Parse(input.Substring(j - 1, 1)); }
                }

                // 島を数える
                int island = CountIsland(map);
                Console.WriteLine(island);
            }
        }
    }
}

