    class Program
    {
        sbyte[,] map;
        const int WIDTH = 12;
        int count;

        public void Proc()
        {
            while (true)
            {
                // initialize
                this.map = new sbyte[WIDTH + 2, WIDTH + 2];
                this.count = 0;
                //setMap
                string input = Console.ReadLine();
                if (string.IsNullOrEmpty(input)) break;

                for (int i = 1; i <= WIDTH; i++)
                {
                    for (int j = 0; j < WIDTH; j++)
                    {
                        this.map[i, j + 1] = sbyte.Parse(input.Substring(j, 1));
                    }
                    if (i != WIDTH) input = Console.ReadLine();
                }

                count += checkMap();

                Console.WriteLine(count);
                if (string.IsNullOrEmpty(input = Console.ReadLine())) continue;
            }
        }

        static void Main(string[] args)
        {
            Program prg = new Program();
            prg.Proc();
        }

        public int checkMap()
        {
            int res = 0;

            for (int x = 1; x <= WIDTH; x++)
            {
                for (int y = 1; y <= WIDTH; y++)
                {
                    if (this.map[y, x] == 1)
                    {
                        countIsland(y, x);
                        res++;
                    }
                }
            }
            return res;
        }

        public void countIsland(int y, int x)
        {
            this.map[y, x] = -1;
            checkAround(y, x);
            this.count++;
        }

        public void checkAround(int y, int x)
        {
            checkRight(y, x);
            checkLeft(y, x);
            checkDown(y, x);
            checkUp(y, x);
        }
        public void checkRight(int y, int x)
        {
            if (this.map[y, x + 1] == -1) return;
            if (this.map[y, x + 1] == 1)
            {
                this.map[y, x + 1] = -1;
                checkAround(y, x + 1);
            }
        }

        public void checkLeft(int y, int x)
        {
            if (this.map[y, x - 1] == -1) return;
            if (this.map[y, x - 1] == 1)
            {
                this.map[y, x - 1] = -1;
                checkAround(y, x - 1);
            }
        }

        public void checkDown(int y, int x)
        {
            if (this.map[y + 1, x] == -1) return;
            if (this.map[y + 1, x] == 1)
            {
                this.map[y + 1, x] = -1;
                checkAround(y + 1, x);
            }
        }

        public void checkUp(int y, int x)
        {
            if (this.map[y - 1, x] == -1) return;
            if (this.map[y - 1, x] == 1)
            {
                this.map[y - 1, x] = -1;
                checkAround(y - 1, x);
            }
        }


    }
}

