        static void Main()
        {
            StringBuilder sb = new StringBuilder();

            string input;

            while (!string.IsNullOrEmpty(input = Console.ReadLine()))
            {
                char[][] map = new char[12][];

                map[0] = input.ToCharArray();

                for (int i = 1; i < 12; i++)
                {
                    map[i] = Console.ReadLine().ToCharArray();
                }

                int count = 0;

                for (int i = 0; i < 12; i++)
                {
                    for (int j = 0; j < 12; j++)
                    {
                        if (map[j][i] == '1')
                        {
                            SearchIsland(map, j, i);
                            count++;
                        }
                    }
                }
                sb.AppendLine(count.ToString());
                Console.ReadLine();
            }
            Console.Write(sb);
        }

        static void SearchIsland(char[][] map, int x, int y)
        {
            int[] moveX = new int[] { -1, 0, +1, 0 };
            int[] moveY = new int[] { 0, +1, 0, -1 };

            Stack<Point> stk = new Stack<Point>();

            Point willPush;
            willPush.x = x;
            willPush.y = y;
            stk.Push(willPush);

            while (stk.Count > 0)
            {
                Point temp = stk.Pop();
                map[temp.x][temp.y] = '3';

                for (int i = 0; i < 4; i++)
                {
                    willPush.x = temp.x + moveX[i];
                    willPush.y = temp.y + moveY[i];

                    if (IsValid(willPush.x, willPush.y) && map[willPush.x][willPush.y] == '1')
                    {
                        stk.Push(willPush);
                    }
                }
            }
        }

        static bool IsValid(int x, int y)
        {
            if (x < 0) return false;
            if (x > 11) return false;
            if (y < 0) return false;
            if (y > 11) return false;
            return true;
        }
    }
