namespace AOJ
{
    class Program
    {
        static IEnumerable<int> Read()
        {
            while(true)
            {
                var d = new int[14, 14];
                for(int i = 1; i < 13; i++)
                {
                    string line;
                    if ((line = Console.ReadLine()) == null) yield break;
                    for(int j = 1; j < 13; j++)
                    {
                        d[i, j] = line[j - 1] - '0';
                    }
                }
                int s = 2;
                for(int i = 1; i < 13; i++)
                    for(int j = 1; j < 13; j++)
                    {
                        if (d[i, j] != 1) continue;
                        if (d[i - 1, j] > 1)
                        {
                            d[i, j] = d[i - 1, j];
                            if (d[i, j - 1] <= 1) continue;
                            int r = d[i, j - 1];
                            for(int k = 1; k < 13; k++)
                                for(int l = 1; l < 13; l++)
                                {
                                    if (d[k, l] == r) d[k, l] = d[i, j];
                                }
                        }
                        else if (d[i, j - 1] > 1) d[i, j] = d[i, j - 1];
                        else
                        {
                            d[i, j] = s;
                            s++;
                        }
                    }
                var dic = new HashSet<int>();
                for(int i = 1; i < 13; i++)
                    for(int j = 1; j < 13; j++)
                        if (d[i, j] > 0)
                            dic.Add(d[i, j]);
                yield return dic.Count;
            }
        }

        static void Main(string[] args)
        {
            foreach (var num in Read())
            {
                Console.WriteLine(num);
                Console.ReadLine();
            }
        }
    }
}

