    public static void goDfs(int[,] map, int[] dx, int[] dy, int x, int y)
    {
        map[x, y] = 0;
        for (int i = 0; i < dx.Length; i++)
        {
            var nx = x + dx[i];   var ny = y + dy[i];
            if (nx >= 0 && nx < 12 && ny >= 0 && ny < 12 && map[nx, ny] == 1) goDfs(map, dx, dy, nx, ny);
        }
    }
