			}

			//delete area
			Stack<Point> mapPoint = new Stack<Point> (10);
			int count = 0;

			Point[] offset = { 
				 new Point (1, 0), 
				 new Point (-1, 0), 
				 new Point (0, 1), 
				 new Point (0, -1), 
			};

			for (int col = 1; col < limit - 1; col++)
			{
				for (int row = 1; row < limit2 - 1; row++)
				{
					if (map[col][row] == Exist)
					{
						mapPoint.Push (new Point (col, row));

						while (mapPoint.Count != 0)
						{
							Point item = mapPoint.Pop ();
							map[item.X][item.Y] = NotExist;

							for (int lp = 0; lp < offset.Length; lp++)
							{
								Point p = item.Offset (offset[lp]);

								if (map[p.X][p.Y] == Exist)
								{
									mapPoint.Push (p);
								}
							}
						}

						count++;
					}
				}
			}

			return count;
		}

