				var atlas = Enumerable.Repeat(-1, 12).Select(
					x => cin.ReadLine.ToCharArray().Select(c=>c=='1').ToArray()
				).ToArray();
				var islandC = 0;
				for(int i = 0; i < 12; i++) {
