						islandC++;
						var S = new Stack<Cartes>();
						S.Push(new Cartes(i, j));
						while(S.Any()) {
							var c = S.Pop();
							atlas[c.y][c.x] = false;
							if(c.x > 0 && atlas[c.y][c.x - 1]) S.Push(new Cartes(c.y, c.x - 1));
							if(c.x < 11 && atlas[c.y][c.x + 1]) S.Push(new Cartes(c.y, c.x + 1));
							if(c.y > 0 && atlas[c.y - 1][c.x]) S.Push(new Cartes(c.y - 1, c.x));
							if(c.y < 11 && atlas[c.y + 1][c.x]) S.Push(new Cartes(c.y + 1, c.x));
						}
					}
				}
				Console.WriteLine(islandC);
