        {
            int ret = 0;
            for (int i = 1; i <= 12; i++)
            {
                bool connect = false;
                for (int j = 1; j <= 12; j++)
                {
                    if (map[i, j] == 1)
                    {
                        // 上の行が島かチェック
                        if (map[i - 1, j] == 1) { connect = true; }

                        // 右が海(0)になったら現在の行が独立した島かチェック
                        if (map[i, j + 1] == 0)
                        {
                            if (!connect) { ret++; }
                            connect = false;
                        }
                    }
                }
            }

            return ret;
