namespace ConsoleApplication1
{
    class SearchIsland
    {
        struct Position { internal int x, y; }

        readonly int[] moveX = new int[] { -1, 0, 1, 0 };
        readonly int[] moveY = new int[] { 0, -1, 0, 1 };

        bool[,] map;

        public void MakeMap(string[] source)
        {
            map = new bool[12, 12];

            for (int i = 0; i < 12; i++)
            {
                for (int j = 0; j < 12; j++)
                {
                    map[i, j] = source[i][j] == '1' ? true : false;
                }
            }
        }

        public int Count()
        {
            int count = 0;

            for (int i = 0; i < 12; i++)
            {
                for (int j = 0; j < 12; j++)
                {
                    if (map[i, j])
                    {
                        CheckIsland(i, j);
                        count++;
                    }
                }
            }
            return count;
        }

        private void CheckIsland(int i, int j)
        {
            Stack<Position> stk = new Stack<Position>();

            Position willPush;
            willPush.x = j;
            willPush.y = i;
            stk.Push(willPush);

            while (stk.Count > 0)
            {
                Position temp = stk.Pop();
                int posX = temp.x;
                int posY = temp.y;

                map[posY, posX] = false;

                for (int n = 0; n < 4; n++)
                {
                    willPush.x = posX + moveX[n];
                    willPush.y = posY + moveY[n];

                    if (isValid(willPush.x, willPush.y) && map[willPush.y, willPush.x])
                    {
                        stk.Push(willPush);
                    }
                }
            }
        }

        private bool isValid(int x, int y)
        {
            if (x < 0 || x > 11) return false;
            if (y < 0 || y > 11) return false;
            return true;
        }
    }

    class Program
    {
        static void Main(string[] args)
        {
            StringBuilder sb = new StringBuilder();
            SearchIsland SI = new SearchIsland();

            string input;

            while (!string.IsNullOrEmpty(input = Console.ReadLine()))
            {
                string[] map = new string[12];
                map[0] = input;

                for (int i = 1; i < 12; i++)
                {
                    map[i] = Console.ReadLine();
                }

                SI.MakeMap(map);

                sb.AppendLine(SI.Count().ToString());

                Console.ReadLine();
            }
            Console.Write(sb);
        }
    }
}

