        private static void Check(int i, int j, int[,] map)
        {
            if (map[i + 1, j] == 1)
            {
                map[i + 1, j] = 0;
                Check(i + 1, j, map);
            }
            if (map[i - 1, j] == 1)
            {
                map[i - 1, j] = 0;
                Check(i - 1, j, map);
            }
            if (map[i, j + 1] == 1)
            {
                map[i, j + 1] = 0;
                Check(i, j + 1, map);
            }
            if (map[i, j - 1] == 1)
            {
                map[i, j - 1] = 0;
                Check(i, j - 1, map);
            }
        }

