def solve(island):
    visited = []
    for lst in island:
        visit_row = []
        for c in lst:
            if c == '1':
                visit_row.append(False)
            else:
                visit_row.append(True)
        visited.append(visit_row)

    def paintable(x, y):
        if 0 <= x < 12 and 0 <= y < 12 and not visited[x][y]:
            return True
        else:
            return False

    def paint(x, y, number):
        visited[x][y] = True
        island[x][y] = number
        if paintable(x-1, y):
            paint(x-1, y, number)

        if paintable(x+1, y):
            paint(x+1, y, number)

        if paintable(x, y-1):
            paint(x, y-1, number)

        if paintable(x, y+1):
            paint(x, y+1, number)

    paint_id = 2

    for i in range(12):
