def fill(x, y, board):
    board[y][x] = 2
    points = [[y, x + 1], [y, x - 1], [y + 1, x], [y - 1, x]]
    # y
    if y == 0:
        points.remove([y-1, x])
    elif y == 11:
        points.remove([y+1, x])
    # x
