        return 0<=new_r<12 and 0<=new_c<12

    def search(self, r, c):
        self.stack.append(self.start_point)
        while self.stack:
            r, c = self.stack.pop()
            self.map[r][c] = '0'
            for (i, j) in [[1, 0], [0, 1], [-1, 0], [0, -1]]:
                if self.check(r+i, c+j):
                    self.stack.append([r+i, c+j])

    def run(self):
        while self.get_start_point():
            r, c = self.start_point
            self.search(r, c)
            self.num_island += 1

