def countIsland(data):
    cnt = [0]
    def countIsland_(data):
        land = findIsland(data)
        if len(land) == 0:
            return cnt[0]
        else:
            data = sinkIsland(data, land)
            cnt[0] = cnt[0] + 1
            return countIsland_(data)

    return countIsland_(data)

# sink adjasent Island.
def sinkIsland(data, land):
    if isIn(*land):
        if not data[land[1]][land[0]] == 0:
            data[land[1]][land[0]] = 0
            lands = [(k[0] + land[0], k[1] + land[1]) for k in XY]
            for x in lands:
                data = sinkIsland(data, x)

    return data

