int main(){
    cin.tie(0);
    ios::sync_with_stdio(false);
    int N,A,B; cin >> N >> A >> B;
    if (A<B) swap(A,B);
    if (B==1){
        cout << pow((mint)2,N) << '\n';
        return 0;
    }
    dp[0][0][0]=1;
    for (int i=0;i<N;++i){
        for (int j=0;j<A;++j){
            dp[i+1][j+1][0]+=dp[i][j][0];
            if (j>=B) dp[i+1][j+1][0]+=dp[i][j][1];
            else dp[i+1][1][0]+=dp[i][j][1];
            if (j+1!=B) dp[i+1][j+1][1]+=dp[i][j][1];
            if (i+B<=N) dp[i+B][min(A,j+B)][1]+=dp[i][j][0];
            dp[i+1][1][1]+=dp[i][j][0];
        }
    }
    mint ans=0,po=1;
    for (int i=N;i>=0;--i,po*=2){
        ans+=(dp[i][A][0]+dp[i][A][1])*po;
    }
    cout << ans << '\n';
}

