inline ll step(ll a, ll x) {
    if (x == 0) {
        return 1;
    }
    if (x % 2 == 1) {
        return step(a, x - 1) * a % mod;
    }
    ll t = step(a, x / 2);
    return t * t % mod;
}

inline ll del(ll a, ll b) {
    return a * step(b, mod - 2) % mod;
}

void count_dp0() {
    ll q, w, e;
    // dp0[0][0] = dp0[0][1] = 1;
    for (q = 0; q < n; q++) {
        /// last are 0s;
        // dp0[q][0] = 1;
        for (w = C[0]; w <= q; w++) {
            dp0[q][0] += dp0[q - w][1];
            dp0[q][0] %= mod;
        }

        /// last are 1s;
        dp0[q][1] = 1;
        for (w = 1; w <= q; w++) {
            dp0[q][1] += dp0[q - w][0];
            dp0[q][1] %= mod;
        }
    }
}

// set <ll> S;

ll getcnt(ll len, bool f) {
    // if (S.find(len) == S.end()) {
    //     cout << "DP0 " << len << " " << dp0[len - 1][1] << endl;
    // }
    // S.insert(len);
    ll ans = dp0[len - 1][1];
    if (f) {
        ans += dp0[len - 1][0];
    }
    return ans;
}

void count_dp() {
    ll q, w, e;
    for (q = 0; q < n; q++) {
        /// last are 0s;
        if (q + 1 < C[0]) {
            dp[q][0] = 1;
        }
        for (w = 1; w < C[0]; w++) {
            dp[q][0] += dp[q - w][1];
            dp[q][0] %= mod;
        }
        /// last are pseudo-1s;
        if (q + 1 < C[1]) {
            dp[q][1] = getcnt(q + 1, 1);
        }
        for (w = 1; w < C[1]; w++) {
            dp[q][1] += dp[q - w][0] * getcnt(w, q == (n - 1));
            dp[q][1] %= mod;
        }
    }
}

