	rep(i,1,B-1)g[i][0]=f[i+1];
	rep(i,1,A-1)g[i][1]=1;
	rep(i,1,n){
		rep(j,i+1,min(n,i+B-1))if(j==n)(g[j][0]+=1LL*g[i][1]*f[j-i+1]%P)%=P;else (g[j][0]+=1LL*g[i][1]*f[j-i]%P)%=P;
		rep(j,i+1,min(n,i+A-1))(g[j][1]+=g[i][0])%=P;
//		printf("g[%d][0]=%d g[%d][1]=%d\n",i,g[i][0],i,g[i][1]);
	}
	printf("%lld\n",((LL)fpow(2,n)-g[n][0]-g[n][1]+P+P)%P);
	return 0;
}

