mint bf(int n,int a,int b){
	set<int> st{0};
	queue<int> que; que.emplace(0);
	const int A=(1<<a)-1;
	const int B=(1<<b)-1;
	while(que.size()){
		int x=que.front(); que.pop();
		rep(i,n-a+1){
			int y=x&~(A<<i);
			if(st.insert(y).Y) que.emplace(y);
		}
		rep(i,n-b+1){
			int y=x|(B<<i);
			if(st.insert(y).Y) que.emplace(y);
		}
	}
	// for(int x:st){rep(i,n) cout<<(x>>i&1); cout NL;}
	return st.size();
}

mint solve(int n,int a,int b){
	if(a>b) swap(a,b);
	vector<mint> el(n+1); //1 or long 0 only
	el[0]=1;
	rep(i,n){
		el[i+1]+=el[i];
		reps(d,a,n)if(i+d+1<=n) el[i+d+1]+=el[i];
	}
	vector<mint> step(n+1);
	rep(d,n+1){
		reps(s,1,a){
			int rem=d-(s+1);
			if(rem>=0 && rem+1<b) step[d]+=el[rem];
		}
	}
	// out(el,step,1);
	vector<mint> dp(n+1);
	rep(d,n+1){
		reps(s,1,a){
			int rem=d-(s+1);
			if(rem>=0 && rem<b) dp[d]+=el[rem];
		}
	}
	rep(i,n)reps(j,i+1,n+1) dp[j]+=dp[i]*step[j-i];
	vector<mint> last(n+1);
	rep(i,n+1)rep(j,n+1)if(i+j<=n){
		int len=i+1;
		if(j>=a) len+=j;
		if(len>=b) continue;
		// out(i,j,el[i],1);
		last[i+j]+=el[i];
	}
	mint re=0;
	rep(i,n+1)rep(j,n+1)if(i+j==n){
		int len=i;
		if(j>=a) len+=j;
		if(len>=b) continue;
		// out(i,j,el[i],1);
		re+=el[i];
	}
	reps(i,1,n+1){
		re+=dp[i]*last[n-i];
	}
	return modpow(2,n)-re;
}

