        dp[i][j]+=dp[i-1][j-1];

      // put '0'*k '1'
      if(i>=(a+1) and j>=(a+1))
        dp[i][j]+=sm[i-(a+1)][j-(a+1)];
    }

    ac[i+1]=ac[i];
