	for(int i=B; i<=N; i++){
		dpb[i] = dpb[i-1];
		for(int j=B; j<=i; j++){
			if(j==i) dpb[i] = (dpb[i] + 1) % mod;
			else dpb[i] = (dpb[i] + dpb[i-j-1]) % mod;
		}
	}
